import React, { useState, useEffect } from 'react';
import { useAuth } from '../AuthContext';
import { useNavigate } from 'react-router-dom';
import { socket } from '../services/socket';
import AIQuestionGenerator from '../components/AIQuestionGenerator';
import './DashboardPage.css';

export default function DashboardPage() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [gameCode, setGameCode] = useState('');
  const [publicGames, setPublicGames] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchPublicGames();
  }, []);

  const fetchPublicGames = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/games');
      const games = await response.json();
      setPublicGames(games);
    } catch (error) {
      console.error('Error fetching games:', error);
    }
  };

  const handleCreateGame = () => {
    setLoading(true);
    socket.connect();
    socket.emit('createGame', {
      hostId: user.uid,
      displayName: user.displayName || user.email,
      isPublic: true
    });

    socket.on('gameCreated', ({ gameId }) => {
      setLoading(false);
      navigate(`/lobby/${gameId}`);
    });

    socket.on('error', ({ error }) => {
      setLoading(false);
      alert('Error creating game: ' + error);
    });
  };

  const handleJoinGame = () => {
    if (!gameCode.trim()) {
      alert('Please enter a game code');
      return;
    }
    navigate(`/lobby/${gameCode}`);
  };

  const handleJoinPublicGame = (gameId) => {
    navigate(`/lobby/${gameId}`);
  };

  return (
    <div className="dashboard-page">
      <header className="dashboard-header">
        <div className="user-info">
          <h2>Welcome, {user?.displayName || user?.email}!</h2>
          <div className="user-actions">
            <button onClick={() => navigate('/profile')} className="btn btn-secondary">
              Profile
            </button>
            <button onClick={logout} className="btn btn-outline">
              Logout
            </button>
          </div>
        </div>
      </header>

      <main className="dashboard-main">
        <div className="game-actions">
          <div className="create-game-section">
            <h3>🎮 Create New Game</h3>
            <p>Start a new quiz game and invite friends to join</p>
            <button 
              onClick={handleCreateGame} 
              className="btn btn-primary btn-large"
              disabled={loading}
            >
              {loading ? 'Creating...' : 'Create Game'}
            </button>
          </div>

          <div className="join-game-section">
            <h3>🔗 Join Game</h3>
            <p>Enter a 6-digit game code to join an existing game</p>
            <div className="join-form">
              <input
                type="text"
                placeholder="Enter game code"
                value={gameCode}
                onChange={(e) => setGameCode(e.target.value)}
                maxLength="6"
                className="game-code-input"
              />
              <button onClick={handleJoinGame} className="btn btn-secondary">
                Join Game
              </button>
            </div>
          </div>
        </div>

        <div className="public-games-section">
          <h3>🌐 Public Games</h3>
          <p>Join games that are open to everyone</p>
          <div className="games-list">
            {publicGames.length === 0 ? (
              <p className="no-games">No public games available at the moment</p>
            ) : (
              publicGames.map(game => (
                <div key={game.id} className="game-card">
                  <div className="game-info">
                    <h4>Game #{game.id}</h4>
                    <p>Players: {game.players?.length || 0}</p>
                    <p>Host: {game.players?.[0]?.displayName || 'Unknown'}</p>
                  </div>
                  <button 
                    onClick={() => handleJoinPublicGame(game.id)}
                    className="btn btn-primary"
                  >
                    Join
                  </button>
                </div>
              ))
            )}
          </div>
        </div>
      </main>
    </div>
  );
}
