import React, { useState } from 'react';
import mockAuth from '../services/mockAuth';
import mockDb from '../services/mockDb';
import { useNavigate, Link } from 'react-router-dom';
import './AuthPage.css';

export default function RegisterPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [displayName, setDisplayName] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    try {
      const userCredential = await mockAuth.createUserWithEmailAndPassword(email, password);
      await mockAuth.updateProfile(userCredential.user, { displayName });
      await mockDb.collection('users').doc(userCredential.user.uid).set({
        email,
        displayName,
        stats: { gamesPlayed: 0, wins: 0, correctAnswers: 0 }
      });
      navigate('/dashboard');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-page">
      <div className="auth-container">
        <div className="auth-header">
          <h1>ðŸŽ¯ QuizMaster</h1>
          <h2>Join the Fun!</h2>
          <p>Create your account and start playing quizzes</p>
        </div>

        <form onSubmit={handleRegister} className="auth-form">
          <div className="input-group">
            <input 
              type="text" 
              placeholder="Display Name" 
              value={displayName} 
              onChange={e => setDisplayName(e.target.value)} 
              required 
              disabled={loading}
            />
          </div>

          <div className="input-group">
            <input 
              type="email" 
              placeholder="Email address" 
              value={email} 
              onChange={e => setEmail(e.target.value)} 
              required 
              disabled={loading}
            />
          </div>

          <div className="input-group">
            <input 
              type="password" 
              placeholder="Password" 
              value={password} 
              onChange={e => setPassword(e.target.value)} 
              required 
              disabled={loading}
            />
          </div>

          {error && (
            <div className="error-message">
              {error}
            </div>
          )}

          <button type="submit" className="btn btn-primary btn-large" disabled={loading}>
            {loading ? 'Creating Account...' : 'Create Account'}
          </button>
        </form>

        <div className="auth-footer">
          <p>
            Already have an account? <Link to="/login">Sign in here</Link>
          </p>
        </div>
      </div>
    </div>
  );
}
